const state = {
    showIcon: false // Initial state
  };
  
  const mutations = {
    setShowIcon(state, value) {
      state.showIcon = value;
    }
  };
  
  const actions = {
    toggleIconVisibility({ commit }, value) {
      commit("setShowIcon", value);
    }
  };
  
  export default {
    state,
    mutations,
    actions
  };
